import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  input:number=0;
  output=0;
  title = 'currency';

  base:any=0;
  to:any=0;
  res='1.00'

  convert(c:any,a:any,b:any){
    this.input=c;
    this.base=Number(a);
    this.to=Number(b);
     this.output= Math.round(this.input * (this.to/this.base));
     this.res=this.output.toString().concat(".00").toString();
     console.log(this.base);
     console.log(this.to);
     console.log(this.input);
  }
}
<html>
  <head>
    <title>currency converter</title>
  </head>
  <body>
    <form onsubmit="return false">
    <div class="main">
    <div class="main-container">
      <div class="selection-container">
        <select #c1 name="" id="fromCurrency" value="">
          <option  value="1.126735">USD</option>
          <option  value="0.876893">GBP</option>
          <option value="79.677056">INR</option>
        </select>
        <span>from</span>
        <input #inp type="number" value=0  name="input" id="amount" min="0" >
        <span>to</span>
        <select #c2 name="" id="toCurrency" value="" >
          <option  value="1.126735">USD</option>
          <option  value="0.876893">GBP</option>
          <option value="79.677056">INR</option>
        </select>
      </div>
      <button id="submitButton" (click)="convert(inp.value,c1.value,c2.value)" >SUBMIT</button>
      <div id="result">
          <div id="resValue">{{res}}</div>
      </div>
    </div>
    </div>
    </form>
  </body>
</html>
